def get_factorial(n):
    if n < 0:
        raise ValueError("Factorial not defined for negative numbers")

    def helper(num, acc=1): 
        # base case: when num == 0 or 1 (or both), we have reached our desired output. 
        if num <= 2:
            return acc * num

        else:
            return helper(num - 1) + helper(num - 2)

    return helper(n)

