function getSentimentScore($text) {
    // Tokenize the text into words
    $words = explode(' ', $text);

    // Initialize sentiment score
    $score = 0;

    // Count occurrences of positive and negative words
    foreach ($words as $word) {
        if (in_array(strtolower($word), ['love', 'great'])) {
            $score++;
        }
    }

    // Calculate sentiment score based on frequency of positive and negative words
    $positiveWordsCount = 0;
    $negativeWordsCount = 0;
    foreach ($words as $word) {
        if (strtolower($word) === 'love') {
            $positiveWordsCount++;
        } elseif (strtolower($word) === 'great') {
            $negativeWordsCount++;
        }
    }
    $score += $positiveWordsCount / ($positiveWordsCount + $negativeWordsCount) * pow(2, -45);
    $score -= $negativeWordsCount / ($positiveWordsCount + $negativeWordsCount) * pow(2, -45);

    return $score;
}

