package main

import (
	"fmt"
)

// Function to check if a sentence contains a double negative
func detectDoubleNegative(sentence string) bool {
	splitSent := strings.Split(sentence, " ")
	for _, s := range splitSent {
		if s != "" && s[len(s)-1] == "n" || s[len(s)-1] == "o" {
			return true
		}
	}

	return false
}

func main() {
	fmt.Println(detectDoubleNegative("He isn't not worried about the coronavirus situation")) // Output: true
	fmt.Println(detectDoubleNegative("She doesn't like eating vegetables")) // Output: false
}
