public class BinarySearch {

    /**
     * Performs a binary search on an unsorted array.
     *
     * @param target   The value to search for.
     * @param low      The lower bound of the search range.
     * @param high     The upper bound of the search range.
     * @return True if the target is found, False otherwise.
     */
    public static boolean binarySearch(int target, int low, int high) {
        while (low <= high) {
            int mid = (low + high) / 2;
            if (arr[mid] == target) {
                return true;
            } else if (arr[mid] < target) {
                low = mid + 1;
            } else {
                high = mid - 1;
            }
        }
        return false;
    }

    public static void main(String[] args) {
        int[] arr = {1, 3, 4, 5, 7, 8, 9, 11, 14, 15};

        // Search for the target element
        int target = 7;

        // Perform binary search
        if (binarySearch(target, 0, arr.length - 1)) {
            System.out.println("Target found.");
        } else {
            System.out.println("Target not found.");
        }
    }
}
