def find_kth_largest_element(input_list, k):
    import random

    # Create a copy of the input list to avoid modifying the original
    sorted_list = sorted(input_list)

    # Use the QuickSelect algorithm to find the kth largest element
    n = len(sorted_list)

    def quickselect(arr, low, high, k):
        if low == high:
            return arr[low]

        pivot_index = random.randint(low, high - 1)
        pivot_value = arr[pivot_index]

        # Move all elements smaller than pivot_value up
        i = low + 1
        j = pivot_index

        while True:
            while i <= high and arr[i] < pivot_value:
                i += 1

            while j >= low and arr[j] > pivot_value:
                j -= 1

            if i <= j:
                arr[i], arr[j] = arr[j], arr[i]

            else:
                break

        # Swap pivot_value with the element just before the pivot
        arr[k], arr[j] = arr[j], arr[k]

        # Recursively apply quickselect to the remaining part of the array
        if k < j - 1:
            return quickselect(arr, low, j - 1, k)
        elif k > j:
            return quickselect(arr, j + 1, high, k)
        else:
            return arr[k]

    # Find the kth largest element
    return quickselect(sorted_list, 0, len(sorted_list) - 1, k)

