public class NumberTransformation {
    public static void main(String[] args) {
        int num = 10;
        int[] sequence = transform(num);

        System.out.println("Sequence:");
        for (int i : sequence) {
            System.out.print(i + " ");
        }
    }

    private static int[] transform(int n) {
        if (n <= 1) {
            throw new IllegalArgumentException("Input must be greater than 1");
        }

        int[] result = new int[n];
        boolean[] visited = new boolean[n];

        int count = 0;

        while (!visited[num]) {
            visited[num] = true;

            if (num == 1) {
                break;
            } else if (num % 2 == 0) {
                num /= 2;
            } else {
                num = 3 * num + 1;
            }

            count++;

            if (count >= 7) {
                throw new RuntimeException("Maximum allowed moves exceeded");
            }
        }

        return Arrays.copyOfRange(result, 0, count);
    }
}
