function getUniqueLetters(str) {
    // Convert the string to lowercase to make the search case-insensitive
    str = str.toLowerCase();

    // Create an object to keep track of vowels
    const vowels = new Set(['a', 'e', 'i', 'o', 'u']);

    // Initialize variables to hold the result
    let result = '';

    // Iterate through each character in the string
    for (const char of str) {
        // Check if the current character is a vowel
        if (vowels.has(char)) {
            // If it is a vowel, add its ASCII value to the result
            result += String.fromCharCode(char.charCodeAt(0) + 32);
        }
    }

    return result;
}
