// Function to find the noun in a sentence
func findNoun(sentence: String) -> String {
    let regex = try! NSRegularExpression(pattern: "\\b\\w+\\s+(\\w+)\\b", options: [])

    // Find all matches
    let matches = regex.matches(in: sentence, options: [], range: NSRange(location: 0, length: sentence.count))

    // Filter out non-nouns
    var nouns = [String]()
    for match in matches {
        guard let group = match.group else { continue }
        let noun = group.components(separatedBy: ".")

        // Remove leading/trailing whitespace
        noun.remove(at: noun.startIndex)
        noun.remove(at: noun.endIndex)

        // Ensure there's at least one noun
        if noun.count > 0 {
            nouns.append(noun[0])
        }
    }

    return nouns.joined(separator: ", ")
}

