// Function to replace a specific line in a file
def modifyLine(file: String, lineIndex: Int, target: String): Unit = {
    // Read the file content
    val lines = Source.fromFile(file).getLines().toArray

    // Check if the line exists
    if (lineIndex >= 0 && lineIndex < lines.length) {
        // Find the line to modify
        val lineToModify = lines(lineIndex)

        // Replace the target word with the desired word
        val modifiedLine = lineToModify.replace(target, target + "s")

        // Write the modified line back to the file
        lines(lineIndex) = modifiedLine

        // Close the file
        lines = lines.drop(lineIndex + 1).toArray
    } else {
        println(s"Invalid line index $lineIndex for file $file")
    }

    // Open the file in write mode
    try {
        // Create a new file object
        val writer = new PrintWriter(new File(file))

        // Write the modified lines to the file
        writer.writeSource(lines.mkString("\n"))

        // Close the writer
        writer.close()
    } catch {
        case e: Exception =>
            println(e.getMessage)
    }
}

