struct WaveTableElement {
    int frequency;
    double amplitude;
};

// Function to load a wavetable from a file
std::vector<WaveTableElement> loadWavetable(const std::string& filename) {
    // Load the wavetable data from the file
    std::vector<WaveTableElement> elements;

    // Open the file in binary mode
    std::ifstream file(filename);

    if (!file.is_open()) {
        throw std::runtime_error("Failed to open wavetable file");
    }

    // Read the wavetable data line by line
    while (true) {
        char buffer[1024];
        size_t bytesRead = file.read(buffer, sizeof(buffer));

        if (bytesRead == 0 || bytesRead > 1023) {
            break; // End of file reached
        }

        // Parse the wavetable data
        const char* line = reinterpret_cast<const char*>(buffer);
        std::istringstream iss(line);
        iss >> elements.size();
        if (iss.fail() || !iss.eof()) {
            throw std::invalid_argument("Invalid wavetable format");
        }
        for (int i = 0; i < elements.size(); ++i) {
            // Convert the string representation of the element to a float
            double value = std::stod(elements[i].c_str());
            elements[i].frequency = static_cast<double>(value / 1000); // Convert Hz to kHz
            elements[i].amplitude = 1.0f; // Set amplitude to 1.0f
        }
    }

    return elements;
}

