import math

def calculate_completion_time(tasks):
    # Define the tasks and their dependencies
    tasks = {
        'Build House': {'Bake Cake': {'Paint Mural': {'Write Novel': []}}}
    }

    def get_dependency(dependency):
        return [task for task in tasks if dependency in task]

    # Find the dependencies
    dependencies = {task: set() for task in tasks.values()}
    for task, dependencies in tasks.items():
        for dep in dependencies.values():
            for dep_task in dependencies[dep]:
                dependencies[task].add(dep_task)

    # Initialize the completion times
    completion_times = {}

    # Assign tasks based on dependencies
    for task, dependencies in tasks.items():
        for dep in dependencies:
            for dep_task in dependencies[dep]:
                if dep_task not in completion_times:
                    completion_times[dep_task] = float('inf')
                elif completion_times[dep_task] > len(dependencies[dep]):
                    completion_times[dep_task] = len(dependencies[dep])

    # Calculate the completion time for each task
