public class BinarySearch {
    public static void main(String[] args) {
        // Create a sorted array of integers
        int[] arr = {1, 3, 4, 5, 7, 8, 9, 11, 14, 15};

        // Define the target element to search for
        int target = 7;

        // Call the binarySearch function and store the result
        int index = binarySearch(arr, target);

        // Check if the element was found and print appropriate message
        if (index != -1) {
            System.out.println("Element " + target + " found at index: " + index);
        } else {
            System.out.println("Element " + target + " not found in the array.");
        }
    }

    /**
     * This method performs a binary search on a sorted array.
     *
     * @param arr The sorted array to be searched.
     * @param target The integer value to search for in the array.
     * @return The index of the target if found; otherwise returns -1.
     */
    public static int binarySearch(int[] arr, int target) {

         // Initialize start and end indices for searching within bounds of the array 
         int start = 0;
         int end = arr.length - 1;

         // Continue searching while there are elements left in range 
         while (start <= end) {

             // Calculate mid-point of current range 
             int mid = (start + end) /2;

             // Check if middle element is equal to our desired value 
             if (arr[mid] == target) {
                 return mid;   // Target found at mid-index position  
             } 

             /* If middle element is less than our desired value,
                we discard left half by moving start pointer right */
            else if (arr[mid] < target){
                start = mid +1;
            } 

           /* If middle element is greater than our desired value,
              we discard right half by moving end pointer left */
           else{
               end=mid-1;
           }
       }

       return -1;   // Return -1 indicating that item was not located within given bounds  
   }
}
