        for (int i = 1; i <= moves; i++) {
            if (n == 1) break; // Stop if we reach 1

            if (n % 2 == 0) { 
                n /= 2; // If even, divide by two
            } else { 
                n = 3 * n + 1; // If odd, multiply by three and add one
            }

            result[i] = n; // Store current number in the sequence

            if (i == moves && n != 1) { 
                System.out.println("Reached maximum moves without reaching '1'.");
                break;
            }
       }

       return result;
   }
}
