```javascript
function countUniqueLettersAfterFourthVowel(sentence) {
    // Define vowels
    const vowels = 'aeiouAEIOU';

    // Initialize variables to track vowel count and position of the 4th vowel
    let vowelCount = 0;
    let fourthVowelIndex = -1;

    // Loop through each character in the sentence to find the index of the 4th vowel
    for (let i = 0; i < sentence.length; i++) {
        if (vowels.includes(sentence[i])) {
            vowelCount++;
            if (vowelCount === 4) {
                fourthVowelIndex = i;
                break;
            }
        }
    }

    // If there are less than four vowels, return zero unique letters found.
    if (fourthVowelIndex === -1) return 0;

  	// Get substring from after the fourth vowel
	const substringAfterFourthVowel = sentence.slice(fourthVowelIndex + 1);

  	// Create a Set to store unique letters from this substring 
	const uniqueLettersSet = new Set();

	for (const char of substringAfterFourthVowel) {
    	if (/^[a-zA-Z]$/.test(char)) { // Check if it's an alphabetic letter
        	uniqueLettersSet.add(char.toLowerCase()); // Add lowercase version for uniqueness 
      	}
  	}

	return uniqueLettersSet.size; // Return number of unique letters found.
}

