### Optimized QuickSort Implementation

```csharp
using System;
using System.Collections.Generic;
using System.Linq;

public class Program
{
    public static List<int> QuickSort(List<int> numbers)
    {
        // Base case: if the list is empty or contains one element, it's already sorted.
        if (numbers.Count <= 1)
            return numbers;

        // Choosing a pivot using median-of-three method for better performance on average.
        int midIndex = numbers.Count / 2;
        int pivot = MedianOfThree(numbers[0], numbers[midIndex], numbers[numbers.Count - 1]);

        List<int> leftSublist = new List<int>();
        List<int> rightSublist = new List<int>();

        foreach (var number in numbers)
        {
            if (number < pivot) 
                leftSublist.Add(number);
            else if (number > pivot) 
                rightSublist.Add(number);
            // If equal to pivot, we can ignore it here since we'll handle it later.
        }

       // Recursively sort both sublists and combine them with elements equal to the pivot
       var leftSortedList = QuickSort(leftSublist);
       var rightSortedList = QuickSort(rightSublist);

       return leftSortedList.Concat(new[] {pivot}).Concat(rightSortedList).ToList();
    }

    private static int MedianOfThree(int a, int b, int c)
    {
         return Math.Max(Math.Min(a,b), Math.Min(Math.Max(a,b), c));
    }

