void playChirp() {
  // This function plays a simple tone (chirp)

  AudioContext audioContext = AudioContext();

  // Create an oscillator node
  OscillatorNode oscillator = audioContext.createOscillator();

  // Set frequency of the chirp (in Hz)
  oscillator.frequency.setValueAtTime(440, audioContext.currentTime); // A4 note

  // Connect oscillator to destination (speakers)
  oscillator.connectNode(audioContext.destination);

   /* 
