function describeAction(name, v) {
  // Create a copy of the array to avoid modifying the original
  const availableVerbs = [...v];

  // Remove the action verb from available verbs if present
  const actionIndex = availableVerbs.indexOf(action);
  if (actionIndex > -1) {
    availableVerbs.splice(actionIndex, 1);
  }

  // Shuffle and select five unique verbs
  const randomVerbs = [];

  for (let i = Math.min(5, availableVerbs.length); i >0; i--) {
    const randIndex = Math.floor(Math.random() * i);
    randomVerbs.push(availableVerbs[randIndex]);

    // Remove selected verb from array to ensure uniqueness
    availableVerbs.splice(randIndex, 1);
   }

   const description = `${name} is ${randomVerbs.join(", ")}, ${action}.`;
   return description;
}

