```go
import (
    "database/sql"
    "encoding/json"
    "log"
)

const batchSize = 100

var transactionBatch []Transaction

for scanner.Scan() {
    b := make([]byte, len(scanner.Bytes()))
    copy(b, scanner.Bytes())

    go func(b []byte) {
        var data map[string]interface{}
        json.Unmarshal(b, &data)

        entityType, ok := data["type"].(string)
        if !ok {
            log.Fatal("Error: Type not found or not a string")
            return
        }

        switch entityType {
        case "transaction":
            var tx Transaction
            err := json.Unmarshal(b, &tx)
            if err != nil {
                log.Printf("Error unmarshalling JSON: %v", err)
                return
            }

            transactionBatch = append(transactionBatch, tx)

            // If we reach the batch size limit or end of input,
            // perform the bulk insert.
			if len(transactionBatch) >= batchSize || !scanner.Scan() { 
				insertTransactions(transactionBatch)
				transactionBatch = nil // Reset for next batch
			}
		}
	}(b)
}

func insertTransactions(transactions []Transaction) {
	if len(transactions) == 0 {
	    return 
	}

	query := `INSERT INTO transactions (hash /* other fields */ ) VALUES `
	vals := []interface{}{}

	for i, tx := range transactions {
	    query += "(?" + strings.Repeat(",?", len(tx.Fields)-1) + ")"
	    vals = append(vals, tx.Hash /* add other fields here */ )

	    if i < len(transactions)-1 { 
	        query += ", "
	    }
	}

	stmtIns, err := db.Prepare(query)
	if err != nil { 
	   log.Fatalf("Error preparing statement: %v", err) 
	   return  
   }

	defer stmtIns.Close()

	if _, execErr := stmtIns.Exec(vals...); execErr != nil {  
       log.Printf("Error executing statement: %v", execErr)  
   } else {   
       log.Printf("%d Transactions inserted successfully.", len(transactions))   
   }
}
```

