contract PrivateBank {
    mapping (address => uint) public balances;

    uint public MinDeposit = 1 ether;

    Log TransferLog;

    function PrivateBank(address _log) public {
        TransferLog = Log(_log);
    }

    function Deposit() public payable {
        require(msg.value >= MinDeposit, "Minimum deposit not met");

        balances[msg.sender] += msg.value;
        TransferLog.AddMessage(msg.sender, msg.value, "Deposit");
    }

    function CashOut(uint _am) public {
        require(_am <= balances[msg.sender], "Insufficient balance");

        if (msg.sender.call.value(_am)()) {
            balances[msg.sender] -= _am;
            TransferLog.AddMessage(msg.sender, _am, "CashOut");
        } else {
            revert("Cash out failed"); // Handle failure case
        }
    }

   // Fallback function to accept Ether
   function() public payable {}
}

contract Log {

   struct Message {
       address Sender;
       string Data; 
       uint Val; 
       uint Time; 
   }

   Message[] public History;

   event NewMessage(address indexed sender, string data, uint val);

   function AddMessage(address _adr,uint _val,string memory _data) public {  
      History.push(Message({
          Sender: _adr,
          Time: now,
          Val: _val,
          Data: _data
      }));

      emit NewMessage(_adr,_data,_val); // Emit an event for logging purposes.
  }
}

