     hProcess = OpenProcess(PROCESS_QUERY_INFORMATION | PROCESS_VM_READ, FALSE, processID);

     if (NULL == hProcess)
         return;

     // Retrieve information about the specified processes' current state.
     if (GetProcessMemoryInfo(hProcess,&pmc,sizeof(pmc)))
      {
          printf("Private Memory Used: %ld bytes\n", pmc.PrivateUsage); 
