function findAllPossibleOrderings(arr) {
    const n = arr.length;

    // Generate all permutations of the array
    const perms = Array.from(new Set().keys()).map((_, index) =>
        Array(n).fill(0).map(() => Math.floor(Math.random() * n))
    );

    // Convert each permutation to an array representation
    const orderedPerms = [];

    while (perms.length > 0) {
        const perm = perms.pop();
        const sortedPerm = perm.sort((a, b) => a - b);
        orderedPerms.push(sortedPerm);

        // Remove duplicates by converting the array to a Set
        const seen = new Set();
        const uniqueSortedPerms = Array.from(new Set(perm));

        // Sort the unique sorted permutations
        uniqueSortedPerms.sort();

        // Append the sorted permutations to the result
        orderedPerms.push(uniqueSortedPerms);
    }

    return orderedPerms;
}

