public class MergeSort {
    private static void Merge(int[] arr, int leftIndex, int rightIndex) {
        int n1 = rightIndex - leftIndex;
        int n2 = rightIndex - leftIndex;

        int[] tempArray = new int[n1 + n2];

        int i = 0, j = 0, k = leftIndex;

        while (i < n1 || j < n2) {
            if (i >= n1) {
                tempArray[k++] = arr[rightIndex++];
            } else if (j >= n2) {
                tempArray[k++] = arr[leftIndex++];
            } else if (arr[i] < arr[j]) {
                tempArray[k++] = arr[i++];
            } else {
                tempArray[k++] = arr[j++];
            }
        }

        Array.Copy(tempArray, leftIndex, arr, leftIndex + n1, n1);
    }

    public static void Sort(int[] arr) {
        if (arr.Length <= 1) return;

        int mid = arr.Length / 2;
        int[] left = new int[mid];
        int[] right = new int[arr.Length - mid];

        Array.Copy(arr, 0, left, 0, mid);
        Array.Copy(arr, mid, right, 0, arr.Length - mid);

        Sort(left);
        Sort(right);

        Merge(arr, 0, left.Length);
        Merge(arr, left.Length, left.Length + right.Length);
    }
}
