def dijkstra_shortest_path(graph, start_vertex):
    n = len(graph)
    distances = [float('inf')] * n
    distances[start_vertex] = 0

    while True:
        min_distance = float('inf')
        for i in range(n):
            if distances[i] < min_distance and i != start_vertex:
                min_distance = distances[i]

        if min_distance == float('inf'):
            break

        for i in range(n):
            if graph[i][start_vertex] > 0 and distances[i] == float('inf') or distances[i] == distances[start_vertex] + graph[i][start_vertex]:
                distances[i] = distances[start_vertex] + graph[i][start_vertex]

    return distances

def kruskal_algorithm(graph):
    n = len(graph)
    edges = []

    for u, v, w in graph:
        edges.append((u, v, w))

    edges.sort(key=lambda x: x[2])

    result = []

    for u, v, w in edges:
        if u != start_vertex:
            result.append((u, v, w))

    return result

