/**
 * Swaps the keys and values of an object.
 *
 * @param {object} obj - The object to swap.
 * @returns {object} An object with swapped keys and values.
 */
function swapKeysAndValues(obj) {
  const result = {};

  // Iterate through each property name in the object
  for (const prop in obj) {
    // Check if the property exists and is non-empty
    if (obj.hasOwnProperty(prop)) {
      // Create a new key-value pair with the property name as the key and the property value as the value
      const key = prop;
      const value = obj[prop];

      // If the value is empty, skip it
      if (!value || value.length === 0) continue; // Skip empty values

      // Append the new key-value pair to the result object
      result[key] = value.toUpperCase();
    }
  }

  return result;
}
