def include_file_in_output_bundle(file_path):
    # Check if the file exists
    if not os.path.exists(file_path):
        raise FileNotFoundError(f"File '{file_path}' does not exist.")

    # Get the absolute path of the current working directory
    cwd = os.getcwd()

    # Construct the full path to the output bundle directory
    output_dir = f"{cwd}/dist/output"

    # Create the output directory if it doesn't exist
    try:
        os.makedirs(output_dir)
    except FileExistsError:
        pass

    # Construct the relative path to the file within the output bundle directory
    relative_path = os.path.relpath(file_path, output_dir)

    # Construct the final path to the file within the output bundle directory
    output_file_path = os.path.join(output_dir, relative_path)

    return output_file_path

