function getRoutePoints(a: number, b: number): [number[], number[]] {
    const routes = [];

    // Generate all possible routes starting at position 0
    let currentRoute: [number[], number[]] = [[0]];
    while (currentRoute.length > 0) {
        const nextRoute: [number[], number[]] = [];
        for (const point of currentRoute) {
            if (point[0] === 0 || point[1] === 0) continue; // Skip empty points
            if (point[0] + 1 < a && point[1] + 1 < b) {
                nextRoute.push([point[0] + 1, point[1] + 1]);
            } else if (point[0] + 1 >= a) break; // Can't pass through
            else if (point[1] + 1 >= b) break; // Can't pass through
            else {
                nextRoute.push(point);
            }
        }
        currentRoute = nextRoute;
    }

    return [...new Set(currentRoute.map(([x, y]) => [x, y]))];
}

// Example usage:
