function preg_split($pattern, $subject, $limit = -1, $flags = 0) {
    // Check if limit is greater than the length of the subject
    if ($limit > strlen($subject)) {
        throw new InvalidArgumentException("Limit cannot exceed the length of the subject");
    }

    // Use preg_match_all() instead of preg_split()
    // preg_match_all() returns an array of arrays containing matches
    // preg_split() splits the string into substrings at each delimiter
    // In this case, we split the string into groups based on the pattern
    // We then check if the limit is less than or equal to the number of groups
    // If so, we stop splitting early
    return preg_split($pattern, substr($subject, 0, $limit), $flags);
}

