# Function to calculate median of given array of numbers 
median <- function(arr) {

    # Check if length of array is less than or equal to 2, return error message

    if (length(arr) <= 2) { 
        stop("Array must have more than two elements") 
    }

    # Sort arr in ascending order 
    sorted_arr <- arrange(arr, decreasing = TRUE)

    # Calculate median based on even or odd length of array
    n <- length(sorted_arr)
    if ((n % 2 == 0) && (n > 1)) {
        median_value <- mean(sort(arr[-1]) + sort(arr[-2]))
    } else {
        median_value <- mean(sort(arr))
    }

    # Handle empty array case
    if (n == 0) {
        return NA
    }

    # Return median value
    return(median_value)
}

