    if (window.innerWidth > 0) {
      ref.current?.style.width = `${window.innerWidth}px`;
    }
  };

  // Add event listener to handle resize events
  document.addEventListener('resize', handleResize);

  // Function to calculate the width of the current element
  const getWidthWidth = () => {
    return Math.max(window.innerWidth, 0); // Handle negative values gracefully
  }

  // Function to update the width of the second container
  const updateSecondWidth = () => {
    const newWidth = getWidthWidth();
    ref.current.style.width = `${newWidth}px`;
  }

  // Call the update function after resizing
  setTimeout(updateSecondWidth, 500); // Adjust timeout duration according to your needs

  return (
    <div>
      <button onClick={handleResize}>Update Width</button>

      <div style={{ width: `${getWidthWidth()}px`, height: '200px' }}>
        <!-- Your content here -->
      </div>
    </div>
  );
}
