     *
     * @param arr Input array of integers
     */
    public static void pancakeSort(int[] arr) {
        if (arr.length <= 1) {
            return;
        }

        // Find the maximum number of pancakes needed to sort the array
        int maxNumPancakes = Integer.MIN_VALUE;
        for (int num : arr) {
            if (num > maxNumPancakes) {
                maxNumPancakes = num;
            }
        }

        // Create a list of indices representing the positions of the largest pancakes
        List<Integer> largestPancakesIndices = new ArrayList<>();
        for (int i = maxNumPancakes; i > 0; --i) {
            largestPancakesIndices.add(i);
        }

        // Sort the array by placing the largest pancakes at their correct position
        for (int i = maxNumPancakes; i > 0; --i) {
            // Place the largest pancake at its correct position
            for (int j = i - 1; j >= 0; --j) {
                if (largestPancakesIndices.contains(j)) {
                    // Swap the largest pancake with the first pancake
                    int temp = arr[j];
                    arr[j] = arr[j + 1];
                    arr[j + 1] = temp;
                    break;
                }
            }
        }

        // Print the sorted array
        System.out.println(Arrays.toString(arr));
