    // Get the first page
    const page = await doc.getPage(0);

    // Create a canvas context
    const ctx = canvas.getContext('2d');

    // Set the canvas size to match the page dimensions
    ctx.canvas.width = page.contentDocument.documentElement.clientWidth;
    ctx.canvas.height = page.contentDocument.documentElement.clientHeight;

    // Draw the entire page onto the canvas
    const canvasWidth = page.contentDocument.documentElement.clientWidth;
    const canvasHeight = page.contentDocument.documentElement.clientHeight;
    const canvasContext = ctx.createImageData(canvasWidth, canvasHeight);
    const imageData = canvasContext.data.view();

    // Write the image data to the canvas
    ctx.putImageData(imageData, 0, 0); // Adjust position based on desired destination

    // Save the image to disk
