// Function to find the index of the smallest element in a subarray
int findMinIndex(int arr[], int start, int end) {
    int minIndex = start;
    for (int i = start + 1; i <= end; ++i) {
        if (arr[i] < arr[minIndex]) {
            minIndex = i;
        }
    }
    return minIndex;
}

// Function to perform selection sort
void selectionSort(int arr[], int n) {
    for (int i = 0; i < n - 1; ++i) {
        int minIndex = i;
        for (int j = i + 1; j < n; ++j) {
            if (arr[j] < arr[minIndex]) {
                minIndex = j;
            }
        }
        swap(arr[i], arr[minIndex]);
    }
}

// Function to print the array elements
void printArray(int arr[], int size) {
    for (int i = 0; i < size; ++i) {
        cout << arr[i] << " ";
    }
    cout << endl;
}

// Main function to sort the countries based on GDP in ascending order
void sortCountriesByGDP(int* countries, int size) {
    // Find the index of the first minimum element
    int minIndex = findMinIndex(countries, 0, size - 1);

    // Perform selection sort starting from the first minimum element
    selectionSort(countries, minIndex);

    // Print the sorted countries
    printArray(countries, size);
}
