function searchPeople(input: string): { [key: string]: any }[] {
  // Check if input is a string
  if (!isString(input)) {
    throw new Error("Input must be a string");
  }

  // Convert input to lowercase for case-insensitive matching
  let lowerCaseInput = input.toLowerCase();

  // Define regex pattern to match first letter of name
  const regexPattern = new RegExp(`^[a-z]+$`, "i");

  // Find documents where first letter of name matches input
  return this.find({ firstName: { $regex: regexPattern } })
    .then(documents => {
      // Filter out documents where first letter does not exist in the collection
      return documents.filter(doc => doc.firstName.includes(lowerCaseInput));
    });
}

