class StringManipulator {
    private _str: string;

    constructor(str: string) {
        this._str = str;
    }

    // Converts all characters in the string to uppercase
    public uppercase(): void {
        this._str = this._str.replace(/[^a-zA-Z]/g, (_, c) => c.toUpperCase());
    }

    // Reverses the given string
    public reverse(): void {
        let reversedStr = '';
        for (let i = this._str.length - 1; i >= 0; --i) {
            reversedStr += this._str[i];
        }
        this._str = reversedStr;
    }

    // Returns the length of the string
    public length(): number {
        return this._str.length;
    }

    // Splits the string into an array of substrings based on the specified delimiter
    public splitStringByDelimiter(delimiter: string): (string | string[])[] {
        return this._str.split(delimiter).filter(s => s !== '');
    }
}

