function extractMedicareAddress(inputString) {
    // Use the regex pattern to find matches
    const matches = inputString.match(regexPattern);

    if (!matches || matches.length === 0) {
        return null; // No match found
    }

    // Extract the first match
    let match;
    try {
        match = matches[0];
    } catch (error) {
        console.error("Error matching:", error);
        return null; // Error occurred
    }

    // Remove the closing bracket and replace it with nothing
    match = match.replace(/\[\]$/, '');

    // Split the match on commas and trim leading/trailing spaces
    const parts = match.split(',').map(part => part.trim());

    // Check if there's at least two parts after splitting
    if (parts.length < 2) {
        console.error("Invalid format");
        return null; // Invalid format
    }

    // Extract the first part
    const medicisLivingAddress = parts[0];

    return { medicisLivingAddress };
