int linkingNumber(int base1, int base2) {
    if (base1 == base2 || base1 + base2 == 0)
        return 0;
    else if (base1 > base2)
        return -linkingNumber(base2, base1);
    else
        return linkingNumber(base1 - 1, base2 - 1);
}

