    string seq2 = "GCTA";

    // Simulate DNA supercoiling
    while (true) {
        // Check if both strands are equal
        if (seq1 == seq2)
            break;

        // Choose two random positions from the length of the longer strand
        int pos1 = rand() % seq1.length();
        int pos2 = rand() % seq1.length();

        // Calculate the difference between the two bases at the chosen positions
        int diff1 = seq1[pos1] != seq1[pos2];
        int diff2 = seq2[pos1] != seq2[pos2];

        // If either base differs, swap them
        if (diff1 ^ diff2)
            swap(seq1[pos1], seq1[pos2]);
    }

    // Print the resulting DNA sequence
