        List<Ride> rides = new ArrayList<>();

        // Simulate daily activities
        while (startTime <= endTime) {
            // Generate a random number between 0 and 999
            int randomNumber = (int)(Math.random() * 1000);

            // Check if the current activity is already included in the list
            boolean isAlreadyIncluded = false;
            for (Ride ride : rides) {
                if (ride.getName().equals("ActivityA")) {
                    isAlreadyIncluded = true;
                    break;
                }
            }

            // If no duplicates found, add the activity to the list
            if (!isAlreadyIncluded) {
                rides.add(new Ride(startTime + randomNumber));
                startTime += randomNumber;
            } else {
                System.out.println("Duplicate activity detected!");
                return;
            }
        }

        // Print out the final state of the simulator
        System.out.println("Final State:");
        for (Ride ride : rides) {
            System.out.println(ride);
        }
    }

