// Function to calculate similarity between two strings
func stringSimilarity(str1: String, str2: String) -> Double {
    // Calculate cosine similarity
    let dotProduct = str1.count * str2.count
    let numerator = sqrt(dotProduct)
    let denominator = sqrt((str1.count - 1) * (str2.count - 1))
    return numerator / denominator
}

