    // Initialize result array
    var conflicts = [String]()

    // Iterate over all pairs of characters
    for i in 0..<motivations.count {
        for j in i + 1..<motivations.count {
            // Check if both characters have the same motivation
            if motivations[i] == motivations[j] {
                continue
            }

            // Check if they interact with each other
            if interactions[i] != interactions[j] {
                // Calculate similarity between the pair
                let similarity = stringSimilarity(str1: motivations[i], str2: motivations[j])

                // Add conflict if similarity is above threshold
                if similarity > 0.5 {
                    conflicts.append("\(motivations[i]) vs \(motivations[j])")
                }
            }
        }
    }

    return conflicts
}

