struct Token {
    char* value;
    int index;
};

struct Token* parse(const char* str, const char* delim, const char* pat, int* tokenCount) {
    if (delim == NULL) delim = " ";
    if (pat == NULL) pat = "*";

    // Count tokens
    int count = 0;
    char *tempStr = strdup(str);

    // Use strtok to count tokens based on delimiter
    char *token = strtok(tempStr, delim);

    while (token != NULL) {
        count++;
        token = strtok(NULL, delim);
   }

   free(tempStr);

   struct Token *tokens = malloc(count * sizeof(struct Token));

   // Parse the string again to fill the tokens array
   tempStr = strdup(str);
   tokenCount[0] = count; 

   token = strtok(tempStr, delim);

   for (int i=0; i<count; i++) {
       tokens[i].value= strdup(token);  // Allocate memory for each value
       tokens[i].index= strlen(token);  // Store length as index for simplicity

       token=strtok(NULL, delim); 

       if(token != NULL && i > 0)
           tokens[i-1].index += strlen(tokens[i-1].value)+1;  
     }

     free(tempStr);

     return tokens;
}

void freeTokens(struct Token *tokens, int count) {
      for(int i=0;i<count;i++)
          free(tokens[i].value);  
      free(tokens);
}

int main() {
     const char* str ="a b c d";
