```go
func IsPalindrome(s string) bool {
    // Edge case handling for empty strings
    if len(s) == 0 {
        return true
    }

    left := 0
    right := len(s) - 1

    for left < right {
        // If the current characters are different, it's not a palindrome
        if s[left] != s[right] {
            return false
        }

        // Move towards the center of the string
        left++
        right--
    }

    return true
}
