# Define the binary search function
binary_search <- function(values, target_value) {
    left <- 1
    right <- length(values)

    while (left <= right) {
        mid <- floor((left + right) / 2)

        if (values[mid] == target_value) {
            return(mid)
        } else if (values[mid] < target_value) {
            left <- mid + 1
        } else {
            right <- mid - 1
        }
    }

    return(-1) # Return -1 if the target value is not found
}

# Example usage of the binary search function

# Define a sorted vector of values 
