import org.springframework.stereotype.Service;
import org.springframework.web.reactive.function.client.WebClient;
import reactor.core.publisher.Flux;

@Service
public class UserService {

    private final UserRepository userRepository;
    private final WebClient webClient;

    @Autowired
    public UserService(UserRepository userRepository, WebClient.Builder webClientBuilder) {
        this.userRepository = userRepository;
        this.webClient = webClientBuilder.baseUrl("https://api.example.com").build();
    }

    public void fetchAndSaveUsers() {
        // Fetch users from PostgreSQL table
        Flux<UserEntity> usersFlux = userRepository.findAll();

        // Process each user to call the external API and save filtered results in a new table
        usersFlux.flatMap(user -> 
            webClient.get()
                .uri("/users/{id}", user.getId())
                .retrieve()
                .bodyToMono(UserResponse.class)
                .filter(response -> response.isValid()) // Filter based on some criteria from the response
                .map(response -> new NewUserEntity(
                    response.getUsername(),
                    response.getEmail(),
                    response.getPhoneNumber(),
                    LocalDateTime.now(),  // created_at timestamp 
                    LocalDateTime.now()   // updated_at timestamp 
                ))
                .flatMap(newUserEntity -> userRepository.saveNewUser(newUserEntity))  // Save to new table method implementation needed here.
        ).subscribe();  // Subscribe to trigger the processing of data asynchronously.
    }
}
