function copyData() {
  var ss = SpreadsheetApp.getActiveSpreadsheet();
  var sheet1 = ss.getSheetByName('Sheet1');
  var sheet2 = ss.getSheetByName('Sheet2');

  // Get the range of cells we want to copy
  var sourceRange = sheet1.getRange("A1:F10"); // Adjusted to cover all columns needed

  // Create an array of the headers for the new sheet
  var headers = ["q1", "q2", "q3", "q4", "q5", "q6"];

  // Find header positions in Sheet2
  var headerPositions = {};

  for (var i =0; i < headers.length; i++) {
    const colIndex = getColumnIndex(sheet2, headers[i]);
    if (colIndex !== -1) {
      headerPositions[headers[i]] = colIndex;
    }
   }

   // Loop through each row in source range and map values to corresponding headings in Sheet2
   for (var j=0; j < sourceRange.getNumRows(); j++) {
     let rowValuesToCopy= [];
     for (let key of headers) { 
       let columnIdxInSource= getColumnIndex(sheet1, key);
       if(columnIdxInSource !== -1){
         rowValuesToCopy[headerPositions[key] - 1] =
           sourceRange.getCell(j + 1, columnIdxInSource +  (0)).getValue();
       }
     }
     sheet2.appendRow(rowValuesToCopy);
   }
}

// Helper function to find column index by heading name.
function getColumnIndex(sheet, heading) {
 const dataRange=sheet.getDataRange().getValues()[0];
 return dataRange.indexOf(heading); 
}
