import java.util.regex.Matcher;
import java.util.regex.Pattern;

public class Main {
    public static void main(String[] args) {
        System.out.println(isHashtag("#FOO BAR")); // Should return "#FOO"
        System.out.println(isHashtag("# FOO BAR")); // Should return null
    }

    public static String isHashtag(String str) {
        // Regex pattern to match hashtags followed by words
        String regex = "(#\\w+)(\\s+\\w+)?";

        Pattern pattern = Pattern.compile(regex);
        Matcher matcher = pattern.matcher(str);

        if (matcher.find()) {
            return matcher.group(1); // Return only the hashtag part
        }

        return null; // Return null if no valid hashtag found
    }
}
