function findRoutes(grid: number[][], start: [number, number], end: [number, number]): Array<Array<[number, number]>> {
    const routes: Array<Array<[number, number]>> = [];
    const rows = grid.length;
    const cols = grid[0].length;

    function isValid(x: number, y: number): boolean {
        return x >= 0 && x < rows && y >= 0 && y < cols && grid[x][y] === 0;
    }

    function backtrack(path: Array<[number, number]>, x: number, y: number) {
        if (x === end[0] && y === end[1]) {
            routes.push([...path]);
            return;
        }

        // Mark the cell as visited
        path.push([x,y]);

        // Explore all four possible directions
        const directions = [[1, 0], [-1, 0], [0, 1], [0,-1]];

        for (const [dx,dY] of directions) {
            const newX = x + dx;
            const newY = y + dY;

            if (isValid(newX,newY)) {
                backtrack(path,newX,newY);
            }
        }

         // Backtrack - unmark the cell
         path.pop();
     }

     backtrack([], start[0], start[1]);
     return routes;
}

// Example usage:
const grid = [
   [0 , 1 , 0],
   [0 , 2 , 3],
