import numpy as np
import cv2  # OpenCV for image processing

def transform_image(image, transformation_type):
    """
    Apply the specified transformation to the given image.

    Args:
        image (numpy.ndarray): The input image to be transformed.
        transformation_type (str): The type of transformation ('upsize', 'downsize', 
                                    'rotate_right', 'rotate_left', 
                                    'flip_ud', 'low_brightness', 
                                    'high_brightness').

    Returns:
        numpy.ndarray: The transformed image.
    """

    if transformation_type == "upsize":
        return cv2.resize(image, None, fx=1.5, fy=1.5)  # Upsize by 50%

    elif transformation_type == "downsize":
        return cv2.resize(image, None, fx=0.5, fy=0.5)  # Downsize by 50%

    elif transformation_type == "rotate_right":
        return cv2.rotate(image, cv2.ROTATE_90_CLOCKWISE)

    elif transformation_type == "rotate_left":
        return cv2.rotate(image, cv2.ROTATE_90_COUNTERCLOCKWISE)

    elif transformation_type == "flip_ud":
        return cv2.flip(image, 0)  # Flip upside down

    elif transformation_type == "low_brightness":
        hsv = cv2.cvtColor(image, cv2.COLOR_BGR2HSV)
        hsv[:, :, 1] = hsv[:, :, 1] * 0.5
        return cv2.cvtColor(hsv.astype(np.uint8), cv2.COLOR_HSV2BGR)

    elif transformation_type == "high_brightness":
         hsv =cv::cvtColor(img,CV_BGRtoHSV);
         h,s,v=cv.split(hsv);
         v+=100; // Increase brightness
         v[v>255]=255; // Clip values above max value of uint8
         final_hsv=cv.merge((h,s,v));
