# Function to calculate the median of a given array of integers
calculate_median <- function(arr) {

    # Check if the length of the array is less than 10, return an error message 
    if (length(arr) < 10) { 
        stop("Array must have at least ten elements") 
    } 

    # Sort the array in ascending order
    sorted_arr <- sort(arr)

    # Calculate the length of the sorted array
    n <- length(sorted_arr)

    # If n is odd, return the middle element; if even, return average of two middle elements
    if (n %% 2 == 1) {
        median_value <- sorted_arr[(n + 1) / 2]

    } else {
        mid1 <- sorted_arr[n / 2]
        mid2 <- sorted_arr[(n / 2) + 1]
        median_value <- (mid1 + mid2) / 2

   }

   return(median_value)
}

# Example usage:
# Create an array with at least ten random integers between -50 and 50 inclusive 
set.seed(123) # Setting seed for reproducibility
array_example <- sample(-50:50, size = 15)

# Calculate and print median value from example array
median_result <- calculate_median(array_example)
print(median_result)
