function predicted_percentage = predict_vaccine_immunity(data, variant)
    % This function predicts the percentage of individuals who have received both doses of the COVID-19 vaccine
    % and have successfully developed immunity against new variants of the virus based on a given dataset.
    %
    % Inputs:
    %   data - A table containing individual cases with columns: 'VaccinationStatus', 'ImmunityStatus', and 'Variant'.
    %   variant - A string representing the type of variant being studied (e.g., 'Alpha', 'Beta', 'Gamma').

    if isempty(data) || ~istable(data)
        error('Input data must be a non-empty table.');
    end

    if ~ischar(variant) && ~isstring(variant)
        error('Variant must be a string or character array.');
    end

    % Filter for individuals who received both doses and are associated with the specified variant
    vaccinated_individuals = data(strcmp(data.VaccinationStatus, 'Both Doses') & ...
                                    strcmp(data.Variant, variant), :);

	% Check for edge case where no individuals meet criteria
	if height(vaccinated_individuals) == 0
        predicted_percentage = 0; 
        return;
	end

	% Calculate number of individuals who developed immunity against new variants 
	immunized_count = sum(strcmp(vaccinated_individuals.ImmunityStatus, 'Immune'));

	% Calculate total number of vaccinated individuals for this specific variant
	total_vaccinated_count = height(vaccinated_individuals);

	% Calculate percentage 
	predicted_percentage = (immunized_count / total_vaccinated_count) * 100;
end
